const addReminderButton=document.getElementById("addReminderButton"),addReminderArea=document.getElementById("add-reminder-popup");function generateAddReminderForm(){return'\n    <button id="addReminderCloseButton" class="close-button">X</button>\n    <form action="./controller.php" method="post">\n      <input type="hidden" name="action" value="add-reminder">\n      <table>\n        <tr>\n          <th>\n            <label>Reminder Name</label>\n          </th>\n        </tr>\n        <tr>\n          <td>\n            <input type="text" name="reminderName">\n          </td>\n        </tr>\n        <tr>\n          <th>\n            <label>Location</label>\n          </th>\n        </tr>\n        <tr>\n          <td>\n            <input type="text" name="location">\n          </td>\n        </tr>\n        <tr>\n          <th>\n            <label>Date</label>\n          </th>\n        </tr>\n        <tr>\n          <td>\n            <input type="date" name="date">\n          </td>\n        </tr>\n        <tr>\n          <th>\n            <label>Start Time</label>\n          </th>\n        </tr>\n        <tr>\n          <td>\n            <input type="time" name="startTime">\n          </td>\n        </tr>\n        <tr>\n          <th>\n            <label>End Time</label>\n          </th>\n        </tr>\n        <tr>\n          <td>\n            <input type="time" name="endTime">\n          </td>\n        </tr>\n        <tr>\n          <th>\n            <label>Completed</label>\n          </th>\n        </tr>\n        <tr>\n          <td>\n            <input type="checkbox" name="completed" value="1">\n          </td>\n        </tr>\n        <tr>\n          <th colspan="2">\n            <input type="submit" value="Save reminder">\n          </th>\n        </tr>\n      </table>\n    </form>\n  '}addReminderArea.innerHTML=generateAddReminderForm(),$("#addReminderCloseButton").click(function(){$("#add-reminder-popup").slideUp(500)});