const addTaskButton=document.getElementById("addTaskButton"),addTaskArea=document.getElementById("add-task-popup");function generateAddTaskForm(){return'\n    <button id="addTaskCloseButton" class="close-button">X</button>\n    <form action="./controller.php" method="post">\n      <input type="hidden" name="action" value="add-task">\n      <table>\n        <tr>\n          <th>\n            <label>task Name</label>\n          </th>\n        </tr>\n        <tr>\n          <td>\n            <input type="text" name="taskName">\n          </td>\n        </tr>\n        <tr>\n          <th>\n            <label>Location</label>\n          </th>\n        </tr>\n        <tr>\n          <td>\n            <input type="text" name="location">\n          </td>\n        </tr>\n        <tr>\n          <th>\n            <label>Date</label>\n          </th>\n        </tr>\n        <tr>\n          <td>\n            <input type="date" name="date">\n          </td>\n        </tr>\n        <tr>\n          <th>\n            <label>Start Time</label>\n          </th>\n        </tr>\n        <tr>\n          <td>\n            <input type="time" name="startTime">\n          </td>\n        </tr>\n        <tr>\n          <th>\n            <label>End Time</label>\n          </th>\n        </tr>\n        <tr>\n          <td>\n            <input type="time" name="endTime">\n          </td>\n        </tr>\n        <tr>\n          <th>\n            <label>Completed</label>\n          </th>\n        </tr>\n        <tr>\n          <td>\n            <input type="checkbox" name="completed" value="1">\n          </td>\n        </tr>\n        <tr>\n          <th colspan="2">\n            <input type="submit" value="Save task">\n          </th>\n        </tr>\n      </table>\n    </form>\n  '}addTaskArea.innerHTML=generateAddTaskForm(),$("#addTaskCloseButton").click(function(){$("#add-task-popup").slideUp(500)});